#!/usr/bin/env python3
"""
Plasmid Designer - Creates plasmid sequences with replication origins, MCS sites, and antibiotic markers
Based on FEMS Microbiology Letters 2013 paper on Broad Host Range Plasmids
"""

import re
from typing import List, Tuple, Dict, Optional
from dataclasses import dataclass


@dataclass
class MCSSite:
    """Represents a Multiple Cloning Site"""
    sequence: str
    enzyme: str


@dataclass
class AntibioticMarker:
    """Represents an Antibiotic Resistance Marker"""
    gene_sequence: str
    name: str


class PlasmidDesigner:
    """Designs plasmids with replication origins, MCS sites, and antibiotic markers"""
    
    # Common restriction enzyme recognition sequences
    RESTRICTION_ENZYMES = {
        'EcoRI': 'GAATTC',
        'BamHI': 'GGATCC',
        'HindIII': 'AAGCTT',
        'XbaI': 'TCTAGA',
        'SalI': 'GTCGAC',
        'PstI': 'CTGCAG',
        'KpnI': 'GGTACC',
        'SmaI': 'CCCGGG',
        'XhoI': 'CTCGAG',
        'NdeI': 'CATATG',
        'NotI': 'GCGGCCGC',
        'SpeI': 'ACTAGT',
        'SacI': 'GAGCTC',
        'BglII': 'AGATCT',
        'NcoI': 'CCATGG',
        'SphI': 'GCATGC',
        'ApaI': 'GGGCCC',
        'EcoRV': 'GATATC',
        'ClaI': 'ATCGAT',
        'NheI': 'GCTAGC'
    }
    
    # Replication origins and genes based on FEMS paper - simplified representative sequences
    # Using shorter representative sequences (200-300 bp) instead of full sequences
    REPLICATION_SYSTEMS = {
        'RSF1010': {
            'type': 'IncQ',
            'oriV': ''
        },
        'RK2': {
            'type': 'IncP-1',
            'oriV': ''
        },
        'pBBR1': {
            'type': 'BroadHostRange',
            'oriV': ''
        }
    }
    
    # Antibiotic resistance gene sequences - simplified representative sequences
    ANTIBIOTIC_MARKERS = {
        'Ampicillin': '',
        'Kanamycin': '',
        'Chloramphenicol': '',
        'Tetracycline': ''
    }
    
    def __init__(self, plasmid_type: str = 'RSF1010'):
        """Initialize plasmid designer with a specific plasmid type"""
        self.plasmid_type = plasmid_type
        if plasmid_type not in self.REPLICATION_SYSTEMS:
            raise ValueError(f"Unknown plasmid type: {plasmid_type}")
    
    def read_input_fasta(self, filename: str) -> Tuple[str, str]:
        """Read input FASTA file and return header and sequence"""
        with open(filename, 'r') as f:
            lines = f.readlines()
        
        header = ''
        sequence = ''
        for line in lines:
            line = line.strip()
            if line.startswith('>'):
                header = line[1:]
            else:
                sequence += line.upper()
        
        return header, sequence
    
    def parse_design_file(self, filename: str) -> Tuple[List[MCSSite], List[AntibioticMarker]]:
        """Parse Design.txt file and return MCS sites and antibiotic markers"""
        mcs_sites = []
        antibiotic_markers = []
        
        with open(filename, 'r') as f:
            lines = f.readlines()
        
        for line in lines:
            line = line.strip()
            if not line or line.startswith('**'):
                continue
            
            parts = [p.strip() for p in line.split(',')]
            if len(parts) == 2:
                if parts[0].startswith('Multiple_Cloning_Site') or parts[0].startswith('MCS'):
                    enzyme = parts[1]
                    if enzyme in self.RESTRICTION_ENZYMES:
                        mcs_sites.append(MCSSite(
                            sequence=self.RESTRICTION_ENZYMES[enzyme],
                            enzyme=enzyme
                        ))
                elif parts[0].startswith('Antibiotic_marker') or parts[0].startswith('Antibiotic'):
                    antibiotic_name = parts[1]
                    if antibiotic_name in self.ANTIBIOTIC_MARKERS:
                        antibiotic_markers.append(AntibioticMarker(
                            gene_sequence=self.ANTIBIOTIC_MARKERS[antibiotic_name],
                            name=antibiotic_name
                        ))
        
        return mcs_sites, antibiotic_markers
    
    def construct_plasmid(self, input_sequence: str, mcs_sites: List[MCSSite], 
                         antibiotic_markers: List[AntibioticMarker]) -> str:
        """Construct complete plasmid sequence"""
        # Start with replication origin
        replication_system = self.REPLICATION_SYSTEMS[self.plasmid_type]
        plasmid_sequence = replication_system['oriV']
        
        # Add antibiotic markers
        for marker in antibiotic_markers:
            plasmid_sequence += marker.gene_sequence
        
        # Add MCS sites (create a polylinker)
        mcs_polylinker = ''
        for mcs in mcs_sites:
            mcs_polylinker += mcs.sequence
        
        # Insert input sequence at first MCS site
        if mcs_sites:
            first_mcs = mcs_sites[0].sequence
            if first_mcs in plasmid_sequence:
                # Insert at existing site
                idx = plasmid_sequence.find(first_mcs)
                plasmid_sequence = (plasmid_sequence[:idx+len(first_mcs)] + 
                                  input_sequence + 
                                  plasmid_sequence[idx+len(first_mcs):])
            else:
                # Add MCS polylinker and insert
                plasmid_sequence += mcs_polylinker
                plasmid_sequence += input_sequence
        else:
            # No MCS, just append input
            plasmid_sequence += input_sequence
        
        return plasmid_sequence
    
    def generate_output_fasta(self, plasmid_sequence: str, output_filename: str, header: str = None):
        """Generate output FASTA file"""
        if header is None:
            header = f"Plasmid_{self.plasmid_type}"
        
        with open(output_filename, 'w') as f:
            f.write(f">{header}\n")
            # Write sequence in 80 character lines
            for i in range(0, len(plasmid_sequence), 80):
                f.write(plasmid_sequence[i:i+80] + "\n")
    
    def generate_design_file(self, mcs_sites: List[MCSSite], 
                            antibiotic_markers: List[AntibioticMarker], 
                            output_filename: str):
        """Generate Design.txt file"""
        with open(output_filename, 'w') as f:
            # Write MCS sites
            for i, mcs in enumerate(mcs_sites, 1):
                f.write(f"Multiple_Cloning_Site{i}, {mcs.enzyme}\n")
            
            # Write antibiotic markers
            for i, marker in enumerate(antibiotic_markers, 1):
                f.write(f"Antibiotic_marker{i}, {marker.name}\n")
            
            f.write("** End of File\n")


def main():
    """Main function to run plasmid design"""
    import sys
    
    # Read input
    input_header, input_sequence = PlasmidDesigner().read_input_fasta('Input.Fa')
    
    # Parse design file
    mcs_sites, antibiotic_markers = PlasmidDesigner().parse_design_file('Design.txt')
    
    # Create designer
    designer = PlasmidDesigner(plasmid_type='RSF1010')
    
    # Construct plasmid
    plasmid_sequence = designer.construct_plasmid(input_sequence, mcs_sites, antibiotic_markers)
    
    # Generate outputs
    designer.generate_output_fasta(plasmid_sequence, 'Output.Fa', input_header)
    designer.generate_design_file(mcs_sites, antibiotic_markers, 'Design.txt')
    
    print(f"Plasmid designed successfully!")
    print(f"  Input sequence length: {len(input_sequence)} bp")
    print(f"  Plasmid sequence length: {len(plasmid_sequence)} bp")
    print(f"  MCS sites: {len(mcs_sites)}")
    print(f"  Antibiotic markers: {len(antibiotic_markers)}")


if __name__ == '__main__':
    main()
